(()=>{"use strict";window.onload=function(){document.getElementById("create").addEventListener("click",(async e=>{e.preventDefault(),await async function(){const e=document.getElementById("username").value,n=document.getElementById("email").value,t=document.getElementById("password").value,s=document.getElementById("password-confirm").value;let o=document.getElementsByClassName("input");if(e&&n&&t&&s)if(t==s){const s=JSON.stringify({username:e,email:n,password:t});fetch("https://web2-routexploreapi.herokuapp.com/users/register",{method:"POST",headers:{"Content-Type":"application/json"},body:s}).then((e=>e.json())).then((e=>{console.log(e),console.log("account created")})),window.location.href="../html/home.html"}else document.getElementById("missing-message-container").innerHTML='<p id="missing-message">Passwords do not match</p>';else{let e='<p id="missing-message">Fill in the form, missing: <br>',n=" ",t=" </p>";for(let e of o)e.value||(n+=`${e.name}, `);const s=e+n.slice(0,n.length-2)+t;document.getElementById("missing-message-container").innerHTML=s}}()}))}})();